ALTER TABLE public."co_requisitos" ENABLE ROW LEVEL SECURITY;
ALTER TABLE public."cursos" ENABLE ROW LEVEL SECURITY;
ALTER TABLE public."dados_users" ENABLE ROW LEVEL SECURITY;
ALTER TABLE public."equivalencias" ENABLE ROW LEVEL SECURITY;
ALTER TABLE public."materias" ENABLE ROW LEVEL SECURITY;
ALTER TABLE public."materias_por_curso" ENABLE ROW LEVEL SECURITY;
ALTER TABLE public."pre_requisitos" ENABLE ROW LEVEL SECURITY;
ALTER TABLE public."users" ENABLE ROW LEVEL SECURITY;

CREATE POLICY "co_requisitos_select_public" ON public."co_requisitos" AS PERMISSIVE FOR SELECT TO authenticated USING (true);
CREATE POLICY "cursos_select_public" ON public."cursos" AS PERMISSIVE FOR SELECT TO authenticated USING (true);
CREATE POLICY "dados_users_delete_own" ON public."dados_users" AS PERMISSIVE FOR DELETE TO authenticated USING ((id_user IN ( SELECT users.id_user
   FROM users
  WHERE (users.auth_id = auth.uid()))));
CREATE POLICY "dados_users_insert_own" ON public."dados_users" AS PERMISSIVE FOR INSERT TO authenticated USING (true) WITH CHECK ((id_user IN ( SELECT users.id_user
   FROM users
  WHERE (users.auth_id = auth.uid()))));
CREATE POLICY "dados_users_select_own" ON public."dados_users" AS PERMISSIVE FOR SELECT TO authenticated USING ((id_user IN ( SELECT users.id_user
   FROM users
  WHERE (users.auth_id = auth.uid()))));
CREATE POLICY "dados_users_update_own" ON public."dados_users" AS PERMISSIVE FOR UPDATE TO authenticated USING ((id_user IN ( SELECT users.id_user
   FROM users
  WHERE (users.auth_id = auth.uid())))) WITH CHECK ((id_user IN ( SELECT users.id_user
   FROM users
  WHERE (users.auth_id = auth.uid()))));
CREATE POLICY "equivalencias_select_public" ON public."equivalencias" AS PERMISSIVE FOR SELECT TO authenticated USING (true);
CREATE POLICY "materias_select_public" ON public."materias" AS PERMISSIVE FOR SELECT TO authenticated USING (true);
CREATE POLICY "materias_por_curso_select_public" ON public."materias_por_curso" AS PERMISSIVE FOR SELECT TO authenticated USING (true);
CREATE POLICY "pre_requisitos_select_public" ON public."pre_requisitos" AS PERMISSIVE FOR SELECT TO authenticated USING (true);
CREATE POLICY "users_insert_own" ON public."users" AS PERMISSIVE FOR INSERT TO authenticated USING (true) WITH CHECK ((auth_id = auth.uid()));
CREATE POLICY "users_select_own" ON public."users" AS PERMISSIVE FOR SELECT TO authenticated USING ((auth_id = auth.uid()));
CREATE POLICY "users_update_own" ON public."users" AS PERMISSIVE FOR UPDATE TO authenticated USING ((auth_id = auth.uid())) WITH CHECK ((auth_id = auth.uid()));

COMMIT;
